public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		double b=2;
		float c=5.0f;
		long d=20;
		char e='a';
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
	}
	

}
