package com.tnsif.daytwo;

public class FarEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {1,2,3,4,5};
		for(int i:a) {
			System.out.println(i);
		}
		char b[]= {'a','b','c','d','e'};
		for(char i:b) {
			System.out.println(i);
		}
		String c[]= {"apple","mango","orange","banana","kiwi"};
		for(String i:c) {
			System.out.println(i);
		}
	}

}
