package com.tnsif.daytwo;

public class OperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20;
		System.out.println(++a + a++);
		System.out.println(++a + b++);
		System.out.println(a++ + b++);
		System.out.println(++b + b++);
		System.out.println(--a + a--);
	}

}
