package com.tnsif.daytwo;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		switch(a) {
		case 10:
			System.out.println("Ten");
			break;
		case 20:
			System.out.println("Twenty");
			break;
		case 30:
			System.out.println("Thirty");
			break;
		default:
			System.out.println("Unknown number");	
			break;
		}
	}

}
